package org.jhm69.battle_of_quiz.adapters;import android.view.View;import android.widget.TextView;import androidx.recyclerview.widget.RecyclerView;import org.jhm69.battle_of_quiz.R;import de.hdodenhof.circleimageview.CircleImageView;public class ResultViewHolder extends RecyclerView.ViewHolder {    public View mView;    public CircleImageView myImg, otherImg;    public TextView myName, otherName, infoTv, mySc, othSc;    public ResultViewHolder(View itemView) {        super(itemView);        mView = itemView;        myImg = mView.findViewById(R.id.thisUserImage);        otherImg = mView.findViewById(R.id.otherUserImage);        myName = mView.findViewById(R.id.thisUserName);        otherName = mView.findViewById(R.id.otherUserName);        infoTv = mView.findViewById(R.id.ivz);        mySc = mView.findViewById(R.id.myScore);        othSc = mView.findViewById(R.id.otherScore);    }}