package org.jhm69.battle_of_quiz.db;import android.content.Context;import androidx.room.Database;import androidx.room.Room;import androidx.room.RoomDatabase;import org.jhm69.battle_of_quiz.dao.DaoAccess;import org.jhm69.battle_of_quiz.messege.model.Chat;import org.jhm69.battle_of_quiz.messege.model.Chatlist;import org.jhm69.battle_of_quiz.models.Post;import org.jhm69.battle_of_quiz.models.Users;import org.jhm69.battle_of_quiz.ui.activities.quiz.BattleModel;import org.jhm69.battle_of_quiz.ui.activities.quiz.Result;@Database(entities = {Users.class, BattleModel.class, Result.class, Post.class, Chatlist.class, Chat.class}, version = 1, exportSchema = false)public abstract class QuizResultDatabase extends RoomDatabase {    private static QuizResultDatabase instance;    public static synchronized QuizResultDatabase getInstance(Context context) {        if (instance == null) {            instance = Room.databaseBuilder(context.getApplicationContext(),                    QuizResultDatabase.class, "battle_of_quiz")                    .allowMainThreadQueries()                    .fallbackToDestructiveMigration()                    .build();        }        return instance;    }    public abstract DaoAccess daoAccess();}