package org.jhm69.battle_of_quiz.models;import com.google.gson.annotations.Expose;import com.google.gson.annotations.SerializedName;import java.io.Serializable;@SuppressWarnings({"ALL", "JavaDoc"})public class Question implements Serializable {    private final static long serialVersionUID = -1171483435203674334L;    @SerializedName("A")    @Expose    private String a;    @SerializedName("B")    @Expose    private String b;    @SerializedName("C")    @Expose    private String c;    @SerializedName("D")    @Expose    private String d;    @SerializedName("ans")    @Expose    private int ans;    @SerializedName("difficulty")    @Expose    private int difficulty;    @SerializedName("hint")    @Expose    private String hint;    @SerializedName("img")    @Expose    private String img;    @SerializedName("index")    @Expose    private int index;    @SerializedName("question")    @Expose    private String question;    @SerializedName("time")    @Expose    private int time;    /**     * No args constructor for use in serialization     */    public Question() {    }    /**     * @param difficulty     * @param a     * @param b     * @param img     * @param c     * @param d     * @param question     * @param ans     * @param hint     * @param index     * @param time     */    @SuppressWarnings("JavaDoc")    public Question(String a, String b, String c, String d, int ans, int difficulty, String hint, String img, int index, String question, int time) {        super();        this.a = a;        this.b = b;        this.c = c;        this.d = d;        this.ans = ans;        this.difficulty = difficulty;        this.hint = hint;        this.img = img;        this.index = index;        this.question = question;        this.time = time;    }    public String getA() {        return a;    }    public void setA(String a) {        this.a = a;    }    public String getB() {        return b;    }    public void setB(String b) {        this.b = b;    }    public String getC() {        return c;    }    public void setC(String c) {        this.c = c;    }    public String getD() {        return d;    }    public void setD(String d) {        this.d = d;    }    public int getAns() {        return ans;    }    public void setAns(int ans) {        this.ans = ans;    }    public int getDifficulty() {        return difficulty;    }    public void setDifficulty(int difficulty) {        this.difficulty = difficulty;    }    public String getHint() {        return hint;    }    public void setHint(String hint) {        this.hint = hint;    }    public String getImg() {        return img;    }    public void setImg(String img) {        this.img = img;    }    public int getIndex() {        return index;    }    public void setIndex(int index) {        this.index = index;    }    public String getQuestion() {        return question;    }    public void setQuestion(String question) {        this.question = question;    }    public int getTime() {        return time;    }    public void setTime(int time) {        this.time = time;    }}