package org.jhm69.battle_of_quiz.repository;import android.app.Application;import android.os.AsyncTask;import androidx.lifecycle.LiveData;import org.jhm69.battle_of_quiz.dao.DaoAccess;import org.jhm69.battle_of_quiz.db.QuizResultDatabase;import org.jhm69.battle_of_quiz.ui.activities.quiz.BattleModel;import java.util.List;public class QuizResultRepository {    private final DaoAccess resultDao;    private final LiveData<List<BattleModel>> allBattles;    public QuizResultRepository(Application application) {        QuizResultDatabase database = QuizResultDatabase.getInstance(application);        resultDao = database.daoAccess();        allBattles = resultDao.getAllResult();    }    public void insertBattle(BattleModel battleModel) {        new InsertNoteAsyncTask(resultDao).execute(battleModel);    }    public LiveData<BattleModel> getBattle(String id) {        return resultDao.getResult(id);    }    public BattleModel getBattleOffline(String id) {        return resultDao.getBattleOffline(id);    }    public void deleteBattle(BattleModel battleModel) {        new DeleteBattleModelAsyncTask(resultDao).execute(battleModel);    }    public LiveData<List<BattleModel>> getAllNotes() {        return allBattles;    }    private static class InsertNoteAsyncTask extends AsyncTask<BattleModel, Void, Void> {        private final DaoAccess resultDao;        private InsertNoteAsyncTask(DaoAccess resultDao) {            this.resultDao = resultDao;        }        @Override        protected Void doInBackground(BattleModel... results) {            resultDao.insertResult(results[0]);            return null;        }    }    private static class DeleteBattleModelAsyncTask extends AsyncTask<BattleModel, Void, Void> {        private final DaoAccess resultDao;        private DeleteBattleModelAsyncTask(DaoAccess resultDao) {            this.resultDao = resultDao;        }        @Override        protected Void doInBackground(BattleModel... results) {            resultDao.deleteResult(results[0]);            return null;        }    }}