package org.jhm69.battle_of_quiz.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import org.jhm69.battle_of_quiz.repository.QuizResultRepository;import org.jhm69.battle_of_quiz.ui.activities.quiz.BattleModel;import java.util.List;public class BattleViewModel extends AndroidViewModel {    @SuppressWarnings("CanBeFinal")    public LiveData<List<BattleModel>> battles;    @SuppressWarnings("CanBeFinal")    QuizResultRepository repository;    public BattleViewModel(@NonNull Application application) {        super(application);        repository = new QuizResultRepository(application);        battles = repository.getAllNotes();    }    public void insert(BattleModel result) {        repository.insertBattle(result);    }    public LiveData<BattleModel> get(String id) {        return repository.getBattle(id);    }    public BattleModel getBattle(String id) {        return repository.getBattleOffline(id);    }    public void delete(String result) {        repository.deleteBattle(result);    }}