package org.jhm69.battle_of_quiz.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import org.jhm69.battle_of_quiz.messege.model.Chatlist;import org.jhm69.battle_of_quiz.repository.ChatRepository;import java.util.List;public class ChatViewModel extends AndroidViewModel {    public LiveData<List<Chatlist>> chats;    ChatRepository repository;    public ChatViewModel(@NonNull Application application) {        super(application);        repository = new ChatRepository(application);        chats = repository.getAllUser();    }    public LiveData<List<Chatlist>> getAllUser() {        return repository.getAllUser();    }    public void updateUser(Chatlist admin) {        repository.updateUser(admin);    }    public void insertUser(Chatlist user) {        repository.insertUser(user);    }    public void deleteAdmin(String uniqueId) {        repository.deleteChat(uniqueId);    }}