package org.jhm69.battle_of_quiz.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import org.jhm69.battle_of_quiz.messege.model.Chat;import org.jhm69.battle_of_quiz.repository.ChatRepository;import java.util.List;public class MessageViewModel extends AndroidViewModel {    ChatRepository repository;    public MessageViewModel(@NonNull Application application) {        super(application);        repository = new ChatRepository(application);    }    public LiveData<List<Chat>> getAllChats(String m) {        return repository.getAllMessage(m);    }    public void insertMessage(Chat m) {        repository.insertMessage(m);    }}