package org.jhm69.battle_of_quiz.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import org.jhm69.battle_of_quiz.models.Post;import org.jhm69.battle_of_quiz.repository.PostRepository;import java.util.List;public class PostViewModel extends AndroidViewModel {    public final LiveData<List<Post>> posts;    final PostRepository repository;    public PostViewModel(@NonNull Application application) {        super(application);        repository = new PostRepository(application);        posts = repository.getAllPosts();    }    public void insert(Post result) {        repository.insert(result);    }    public void delete(Post result) {        repository.delete(result);    }}